WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/32.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/32.0/apex.js")}

var namespace_prefix = '';
var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass where Name = 'ApplicationUtilities' LIMIT 1");
var getname = gettingnamespace.getArray("records");
if(getname.length > 0 && getname[0].NamespacePrefix)
{
namespace_prefix = getname[0].NamespacePrefix + '.';
}

var result = sforce.apex.execute(namespace_prefix+"ContractResource","deepCopyContract", {toCopy: "{!Contract.Id}"});
console.log(result);
window.location.href = '/' + result + '/e?retURL=%2F' + result;
